mapper.select:
select tbl_activity.id as id, tbl_user.name as owner, tbl_activity.name as name, tbl_activity.startDate as startDate, tbl_activity.endDate as endDate, tbl_activity.cost as cost, tbl_activity.description as description, tbl_activity.createTime as createTime, tbl_activity.createBy as createBy, tbl_activity.editTime as editTime, tbl_activity.editBy as editBy from tbl_activity left join tbl_user on tbl_activity.owner = tbl_user.id
and tbl_activity.name like "%" #{name} "%"
and tbl_user.name like "%" #{owner} "%"
and tbl_activity.startDate >= #{startDate}
and tbl_activity.endDate <= #{endDate}
order by tbl_activity.createTime desc

select count(tbl_activity.id) from tbl_activity left join tbl_user on tbl_activity.owner = tbl_user.id
and tbl_activity.name like "%" #{name} "%"
and tbl_user.name like "%" #{owner} "%"
and tbl_activity.startDate >=#{startDate}
and tbl_activity.endDate <=#{endDate}
order by tbl_activity.createTime desc
